mapper.insert:
INSERT INTO NOTICE (
	NNO
	, TITLE
	, WRITER
	, CONTENT
	, REGDATE
	, VIEWCNT
	, POINT
	, STARTDATE
	, ENDDATE
	, UPDATEDATE
	, DIST
) VALUES (
	#{nno}
	, #{title}
	, #{writer}
	, #{content}
	, #{regdate}
	, #{viewcnt}
	, #{point}
	, #{startdate}
	, #{enddate}
	, #{updatedate}
	, #{dist}
)

mapper.update:
UPDATE NOTICE
SET    TITLE = #{title}
       , CONTENT = #{content}
       , ENDDATE = #{enddate}
       , UPDATEDATE = #{updatedate}
WHERE  NNO = #{nno}
